let fs = require("fs");

const input = require(`readline-sync`);
let employees = [];
var count=0;

while(true){
let fname = input.question("Enter your first name: ");
let lname = input.question("Enter your last name: ");
let gender = input.question("Enter your gender: ");
let age = input.question("Enter your age:");
var v1= new Date().toLocaleTimeString();

let arr= {id:count, fname:fname,lname:lname,gen:gender, age:age,dat:v1 };
employees.push(arr);


let employeeString = JSON.stringify(employees);
fs.writeFileSync("data.json",employeeString);
count++;
}